<?php
declare(strict_types=1);

namespace GameOfLifePackage\Tests;

include '../vendor/autoload.php';

use GameOfLifePackage\GameOfLife;
use PHPUnit\Framework\TestCase;

class GameOfLifeTest extends TestCase
{

    public function testGameOfLifeCellDies() {
        $gol = $this->generateSampleGameOfLife();

        $cellIndexExpectedToDie = 1823;

        $this->assertEquals(1, $gol->matrix->getCellByIndex($cellIndexExpectedToDie)->getValue());

        $gol->nextGen();

        $this->assertEquals(0, $gol->matrix->getCellByIndex($cellIndexExpectedToDie)->getValue());
    }

    public function testGameOfLifeCellGoesAlive() {
        $gol = $this->generateSampleGameOfLife();

        $cellIndexExpectedToGoAlive = 1820;

        $this->assertEquals(0, $gol->matrix->getCellByIndex($cellIndexExpectedToGoAlive)->getValue());

        $gol->nextGen();

        $this->assertEquals(1, $gol->matrix->getCellByIndex($cellIndexExpectedToGoAlive)->getValue());
    }

    public function testGameOfLifeCellKeepState() {
        $gol = $this->generateSampleGameOfLife();

        $cellIndexExpectedToKeepState = 3680;

        $this->assertEquals(0, $gol->matrix->getCellByIndex($cellIndexExpectedToKeepState)->getValue());

        $gol->nextGen();

        $this->assertEquals(0, $gol->matrix->getCellByIndex($cellIndexExpectedToKeepState)->getValue());
    }

    public function testCountActiveCells() {
        $gol = $this->generateSampleGameOfLife();

        $cellIndex = 1820;

        $neighborCells = $gol->matrix->getNeighborCells($cellIndex);

        $this->assertEquals(3, $gol->countActiveCells($neighborCells));
    }

